import React, { useState, useEffect } from 'react';
import { MapPin } from 'lucide-react';



const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const Page = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex items-center justify-center p-4 sm:p-8 relative bg-white ${className}`}>
      <div className="w-full max-w-md">
        {children}
      </div>
    </div>
  );
  const Timeline = () => {
    const events = [
      { time: "12:00", icon: "👰🤵", label: "Pregătiri", description: "Începutul zilei nunții" },
      { time: "15:00", icon: "⛪", label: "Ceremonie", description: "Ceremonia religioasă" },
      { time: "17:00", icon: "📸", label: "Fotografii", description: "Sesiune foto" },
      { time: "18:00", icon: "🎉", label: "Petrecere", description: "Începe petrecerea" },
    ];

    return (
      <div className="w-full max-w-md mx-auto">
        <div className="relative">
          <div className="absolute top-0 left-6 h-full w-1 bg-black opacity-50"></div>
          <div className="relative">
            {events.map((event, index) => (
              <div key={index} className="mb-8 flex">
                <div className="flex-shrink-0 w-12 h-12 rounded-full bg-white border-2 border-black flex items-center justify-center text-2xl z-10">
                  {event.icon}
                </div>
                <div className="ml-4">
                  <p className="font-bold text-lg text-black">{event.time}</p>
                  <p className="text-md font-medium text-black">{event.label}</p>
                  <p className="text-sm text-black text-opacity-80">{event.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const SaveTheDatePage = () => {
    const [timeLeft, setTimeLeft] = useState({
      days: 0,
      hours: 0,
      minutes: 0,
      seconds: 0
    });
  
    useEffect(() => {
      const calculateTimeLeft = () => {
        const weddingDate = new Date('2025-06-01T15:00:00');
        const now = new Date();
        const difference = weddingDate - now;
  
        if (difference > 0) {
          const days = Math.floor(difference / (1000 * 60 * 60 * 24));
          const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
          const minutes = Math.floor((difference / (1000 * 60)) % 60);
          const seconds = Math.floor((difference / 1000) % 60);
  
          setTimeLeft({ days, hours, minutes, seconds });
        }
      };
  
      const timer = setInterval(calculateTimeLeft, 1000);
      calculateTimeLeft();
  
      return () => clearInterval(timer);
    }, []);
  
    return (
      <div className="bg-black rounded-3xl overflow-hidden shadow-lg h-[700px] relative">
        <div className="absolute inset-0 z-0">
          <img src="/prima.png" alt="Background" className="w-full h-full object-cover" />
        </div>
        <div className="relative z-10 p-8 h-full flex flex-col justify-between items-center text-center">
          <h1 className="font-script text-5xl text-black">Adina & Mihai</h1>
          <div className="font-script text-2xl text-black">
            <h1 className="font-script text-3xl text-black mb-6">Vom deveni o familie în</h1>
            <div className="bg-white rounded-lg shadow-lg px-6 py-4 mb-6 grid grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-3xl font-bold">{timeLeft.days}</div>
                <div className="text-sm">zile</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold">{timeLeft.hours}</div>
                <div className="text-sm">ore</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold">{timeLeft.minutes}</div>
                <div className="text-sm">minute</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold">{timeLeft.seconds}</div>
                <div className="text-sm">secunde</div>
              </div>
            </div>
            {/* <p>Va asteptam cu drag, sa sarbatorim iubirea</p> */}
          </div>
        </div>
      </div>
    );
  };

  const PageContent = ({ children, title, background }) => (
    <div className={`bg-white rounded-3xl overflow-hidden shadow-lg h-[700px] p-8 flex flex-col ${background ? 'relative' : ''}`}>
      {background && (
        <div className="absolute inset-0 z-0">
          <img src={background} alt="Background" className="w-full h-full object-cover" />
        </div>
      )}
      <div className="relative z-10 flex flex-col h-full">
        {title && <h2 className="text-2xl sm:text-3xl tracking-[0.2em] text-center mb-8 text-white">{title}</h2>}
        <div className="flex-grow">
          {children}
        </div>
      </div>
    </div>
  );

  const InvitationDetailsPage = () => (
    <PageContent background="/frunze.png">
      <div className="text-center text-black space-y-4">
        <h1 className="font-script text-5xl mb-2">Adina & Mihai</h1>
        <div className="text-4xl mb-6">💍</div>
        
        <p className="italic">Alături de părinții noștri,</p>
        
        <div className="flex justify-between">
          <p>Lăcrimioara și Dan Sposub</p>
          <p>Gabriela și Sorin Puha</p>
        </div>
        
        <p className="italic">Și împreună cu nașii,</p>
        
        <div className="flex justify-between">
          <div>
            <p>Bianca și Ovidiu Prutean</p>
            <p>Adina și Bogdan Siminiuc</p>
          </div>
          <div>
            <p>Daniela și Victor Romaniuc</p>
            <p>Viorica și Lucian Dumitriu</p>
          </div>
        </div>
        
        <p>Vă invităm să ne fiți alături în ziua nunții noastre,</p>
        
        <p className="font-script text-3xl">Duminică, 9 iunie 2025</p>
        
       
      </div>
    </PageContent>
  );

  const ProgramPage = () => (
    <PageContent background="/program1.png">
      <div className="text-center text-black h-full flex flex-col justify-center items-center">
        <h2 className="font-script text-5xl mb-8">Program</h2>
        <div className="flex-grow flex items-center justify-center">
          <Timeline />
        </div>
      </div>
    </PageContent>
  );

  return (
    <div className="font-['Raleway'] min-h-screen flex flex-col">
      <Page>
        <SaveTheDatePage />
      </Page>

      <Page>
        <InvitationDetailsPage />
      </Page>

      <Page>
        <ProgramPage />
      </Page>

      <Page>
        <PageContent background="/frunze.png">
          <div className="text-center space-y-12 sm:space-y-16 text-black">
            <div>
              <h2 className="font-script text-3xl sm:text-4xl mb-6 sm:mb-8">Locații</h2>
              <div className="space-y-8">
                <div>
                  <p className="font-semibold text-xl mb-2">Ceremonia religioasă</p>
                  <p>Biserica "Nașterea Maicii Domnului" - Frătăuții Noi</p>
                  <a href="https://www.google.com/maps/search/?api=1&query=Biserica+Nașterea+Maicii+Domnului+Frătăuții+Noi" 
                     target="_blank" 
                     rel="noopener noreferrer" 
                     className="inline-block mt-2">
                    <MapPin className="w-6 h-6 text-black hover:text-gray-200" />
                  </a>
                </div>
                <div>
                  <p className="font-semibold text-xl mb-2">Petrecerea</p>
                  <p>Restaurant "Golden Forest" - Sucevița</p>
                  <a href="https://www.google.com/maps/search/?api=1&query=Restaurant+Golden+Forest+Sucevița" 
                     target="_blank" 
                     rel="noopener noreferrer" 
                     className="inline-block mt-2">
                    <MapPin className="w-6 h-6 text-black hover:text-gray-200" />
                  </a>
                </div>
              </div>
            </div>

            <div className="pt-6 sm:pt-8">
              
              <h2 className="font-script text-3xl sm:text-4xl mb-6 sm:mb-8">Confirmare</h2>
              <p className="mt-6">Vă rugăm să ne confirmați prezența până la data de 10 mai 2025.</p>
              <p>Adina: 0743 378 074     Mihai: 0752 557 033</p>
              <p className="font-script text-2xl mt-6">Vă așteptăm cu drag!</p>
              
            </div>
          </div>
        </PageContent>
      </Page>
    </div>
  );
};

const CustomStyles = () => {
  return (
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500;600&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }
    `}</style>
  );
};

const WeddingInvitationWithStyles = () => (
  <div className="bg-gray-100 min-h-screen flex items-center justify-center">
    <div className="w-full max-w-[1000px] mx-auto relative z-10">
      <CustomStyles />
      <WeddingInvitation />
    </div>
  </div>
);

export default WeddingInvitationWithStyles;
