import React, { useState, useEffect } from 'react';
import { MapPin } from 'lucide-react';

const FlowerAnimation = () => {
  return (
    <div className="absolute top-0 left-0 w-1/3 h-full overflow-hidden pointer-events-none">
      <svg 
        xmlns="http://www.w3.org/2000/svg" 
        viewBox="0 0 300 600" 
        className="absolute top-0 left-0 w-full h-full"
      >
        {/* Flower 1 */}
        <g className="animate-flower1">
          <circle cx="50" cy="100" r="15" fill="#FFE4E1" />
          <circle cx="35" cy="85" r="10" fill="#FFC0CB" />
          <circle cx="65" cy="85" r="10" fill="#FFC0CB" />
          <circle cx="50" cy="115" r="10" fill="#FFC0CB" />
        </g>

        {/* Flower 2 */}
        <g className="animate-flower2">
          <circle cx="100" cy="250" r="20" fill="#FAFAD2" />
          <circle cx="80" cy="230" r="15" fill="#DDA0DD" />
          <circle cx="120" cy="230" r="15" fill="#DDA0DD" />
          <circle cx="100" cy="270" r="15" fill="#DDA0DD" />
        </g>
      </svg>
    </div>
  );
};

const Countdown = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const calculateTimeLeft = () => {
      const weddingDate = new Date('2025-06-01T00:00:00');
      const now = new Date();
      const difference = weddingDate - now;

      if (difference > 0) {
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);

        setTimeLeft({ days, hours, minutes, seconds });
      }
    };

    const timer = setInterval(calculateTimeLeft, 1000);
    return () => clearInterval(timer);
  }, []);

  const TimeUnit = ({ value, label }) => (
    <div className="flex flex-col items-center mx-4">
      <div className="text-3xl font-light">{value}</div>
      <div className="text-sm text-gray-600 mt-1">{label}</div>
    </div>
  );

  return (
    <div className="flex justify-center items-center mt-8">
      <TimeUnit value={timeLeft.days} label="days" />
      <TimeUnit value={timeLeft.hours} label="hours" />
      <TimeUnit value={timeLeft.minutes} label="minutes" />
      <TimeUnit value={timeLeft.seconds} label="seconds" />
    </div>
  );
};

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const CrucifixIcon = () => (
    <svg width="24" height="36" viewBox="0 0 24 36" className="inline-block">
      <path
        d="M10 2 L14 2 L14 8 L20 8 L20 12 L14 12 L14 34 L10 34 L10 12 L4 12 L4 8 L10 8 Z"
        fill="#666666"
        stroke="none"
      />
    </svg>
  );

  const Page = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex items-center justify-center p-8 bg-[#F5F5DC] relative ${className}`}>
      <FlowerAnimation />
      <div className="max-w-4xl w-full z-10">{children}</div>
    </div>
  );

  const Timeline = () => {
    const events = [
      { time: "11:00", icon: <CrucifixIcon />, label: "Сбор гостей", x: 100, y: 450 },
      { time: "12:00", icon: "🥂", label: "Фуршет", x: 300, y: 300 },
      { time: "13:00", icon: "💍", label: "Свадебная церемония", x: 200, y: 200 },
      { time: "14:00", icon: "🍽️", label: "Праздничный ужин", x: 400, y: 150 },
      { time: "20:00", icon: "🎂", label: "Завершение вечера", x: 500, y: 50 }
    ];

    return (
      <div className="relative w-[600px] h-[600px] mx-auto">
        {/* SVG for curved dotted line */}
        <svg 
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 600 600"
          style={{ zIndex: 0 }}
        >
          <path
            d="M 100 450 
               C 200 450, 350 350, 300 300
               S 150 250, 200 200
               S 350 180, 400 150
               S 450 100, 500 50"
            stroke="#939393"
            strokeWidth="1"
            strokeDasharray="4 4"
            fill="none"
          />
        </svg>

        {/* Events */}
        {events.map((event, index) => (
          <div
            key={index}
            className="absolute"
            style={{
              left: `${event.x - 100}px`,
              top: `${event.y - 10}px`,
              width: '200px'
            }}
          >
            {/* Time and Label */}
            <div className="absolute left-0 text-left -translate-x-[120px]">
              <div className="text-xl font-light">{event.time}</div>
              <div className="italic text-sm text-gray-700 mt-1">{event.label}</div>
            </div>

            {/* Icon */}
            <div 
              className="absolute text-2xl"
              style={{
                left: '100px',
                transform: 'translateY(-50%)'
              }}
            >
              {event.icon}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="font-['Raleway']">
      {/* Page 1: Save the Date */}
      <Page>
        <div className="max-w-2xl mx-auto px-8">
          <div className="text-center mb-8">
            <div className="inline-block">
              <h2 className="text-3xl font-light tracking-wide">
                Adina
              </h2>
              <h2 className="text-3xl font-light tracking-wide">
                Mihai
              </h2>
            </div>
          </div>
          
          <div className="aspect-[4/4] relative overflow-hidden rounded-2xl bg-black">
            <img 
              src="/m_a.jpg"
              alt="Couple"
              className="w-full h-full object-cover opacity-90 grayscale"
            />
          </div>
          
          <Countdown />
        </div>
      </Page>

      {/* Page 2: Program of the Day */}
      <Page>
        <div className="text-center mb-16">
          <h2 className="text-3xl tracking-[0.2em]">ПРОГРАММА</h2>
        </div>
        <Timeline />
      </Page>

      {/* Page 3: Location and RSVP */}
      <Page>
        <div className="max-w-2xl mx-auto text-center space-y-16">
          <div>
            <h2 className="font-script text-4xl mb-8">Location</h2>
            <div className="space-y-4">
              <div className="flex items-center justify-center gap-2">
                <MapPin className="w-6 h-6 text-neutral-400" />
                <span className="text-xl">Банкетный зал "Нежность"</span>
              </div>
              <p className="text-neutral-600 text-lg">ул. Свадебная, 1</p>
            </div>
          </div>

          <div className="pt-8">
            <h2 className="font-script text-4xl mb-8">Анкета гостя</h2>
            <p className="text-neutral-600 text-lg mb-8">
              Будем сильно рады видеть вас на этом празднике жизни!
            </p>
            <button
              onClick={() => setConfirmed(!confirmed)}
              className={`px-12 py-4 rounded-md transition-all text-lg
                ${confirmed 
                  ? 'bg-neutral-200 text-neutral-800' 
                  : 'bg-neutral-800 text-white'
                }`}
            >
              {confirmed ? 'Подтверждено' : 'Ответить'}
            </button>
          </div>
        </div>
      </Page>
    </div>
  );
};

// Add custom CSS for fonts and animations
const CustomStyles = () => {
  return (
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }

      @keyframes flower1Movement {
        0%, 100% { transform: translate(0, 0) rotate(0deg); }
        25% { transform: translate(10px, -10px) rotate(10deg); }
        75% { transform: translate(-10px, 10px) rotate(-10deg); }
      }

      @keyframes flower2Movement {
        0%, 100% { transform: translate(0, 0) rotate(0deg); }
        25% { transform: translate(-15px, 15px) rotate(-15deg); }
        75% { transform: translate(15px, -15px) rotate(15deg); }
      }

      .animate-flower1 {
        animation: flower1Movement 4s ease-in-out infinite;
      }

      .animate-flower2 {
        animation: flower2Movement 5s ease-in-out infinite;
      }
    `}</style>
  );
};

const WeddingInvitationWithStyles = () => (
  <>
    <CustomStyles />
    <WeddingInvitation />
  </>
);

export default WeddingInvitationWithStyles;
