import React, { useState } from 'react';
import { MapPin } from 'lucide-react';

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const CrucifixIcon = () => (
    <svg width="24" height="36" viewBox="0 0 24 36" className="inline-block">
      <path
        d="M10 2 L14 2 L14 8 L20 8 L20 12 L14 12 L14 34 L10 34 L10 12 L4 12 L4 8 L10 8 Z"
        fill="#666666"
        stroke="none"
      />
    </svg>
  );

  const Page = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex items-center justify-center p-8 bg-white ${className}`}>
      <div className="max-w-4xl w-full">{children}</div>
    </div>
  );

  const Timeline = () => {
    const events = [
      { time: "11:00", icon: <CrucifixIcon />, label: "Сбор гостей", x: 300, y: 50 },
      { time: "12:00", icon: "🥂", label: "Фуршет", x: 350, y: 150 },
      { time: "13:00", icon: "💍", label: "Свадебная церемония", x: 300, y: 250 },
      { time: "14:00", icon: "🍽️", label: "Праздничный ужин", x: 350, y: 350 },
      { time: "20:00", icon: "🎂", label: "Завершение вечера", x: 300, y: 450 }
    ];

    return (
      <div className="relative w-[500px] h-[600px] mx-auto">
        {/* SVG for curved dotted line */}
        <svg 
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 500 600"
          style={{ zIndex: 0 }}
        >
          <path
            d="M 100 50 L 300 50
               C 400 50, 400 150, 350 150
               C 300 150, 300 250, 300 250
               C 300 300, 400 350, 350 350
               C 300 350, 300 450, 300 450"
            stroke="#939393"
            strokeWidth="1"
            strokeDasharray="4 4"
            fill="none"
          />
        </svg>

        {/* Events */}
        {events.map((event, index) => (
          <div
            key={index}
            className="absolute"
            style={{
              left: '20px',
              top: `${event.y - 10}px`,
              width: '400px'
            }}
          >
            {/* Time and Label */}
            <div className="absolute left-0 text-left">
              <div className="text-xl font-light">{event.time}</div>
              <div className="italic text-sm text-gray-700 mt-1">{event.label}</div>
            </div>

            {/* Icon */}
            <div 
              className="absolute text-2xl"
              style={{
                left: `${event.x}px`,
                transform: 'translateY(-50%)'
              }}
            >
              {event.icon}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="font-['Raleway']">
      {/* Page 1: Save the Date */}
      <Page>
        <div className="text-center mb-12">
          <h1 className="font-script text-5xl mb-8">Save the Date</h1>
          <p className="text-2xl mb-2">24 ноября 2024</p>
          <div className="w-full max-w-lg mx-auto h-px bg-neutral-300 my-12" />
        </div>

        <div className="grid grid-cols-2 gap-12">
          <div className="aspect-square overflow-hidden bg-white shadow-lg">
            <img 
              src="/api/placeholder/500/500" 
              alt="Couple"
              className="w-full h-full object-cover"
            />
          </div>
          <div className="flex flex-col justify-center space-y-8">
            <h2 className="font-script text-4xl">Alina & Anton</h2>
            <p className="text-neutral-600 text-lg leading-relaxed">
              В жизни всегда должна оставаться та самая изюминка - наша свадьба!
            </p>
            <p className="text-neutral-600">
              Приглашаем вас разделить с нами радость этого особенного события
            </p>
          </div>
        </div>
      </Page>

      {/* Page 2: Program of the Day */}
      <Page>
        <div className="text-center mb-16">
          <h2 className="text-3xl tracking-[0.2em]">ПРОГРАММА</h2>
        </div>
        <Timeline />
      </Page>

      {/* Page 3: Location and RSVP */}
      <Page>
        <div className="max-w-2xl mx-auto text-center space-y-16">
          <div>
            <h2 className="font-script text-4xl mb-8">Location</h2>
            <div className="space-y-4">
              <div className="flex items-center justify-center gap-2">
                <MapPin className="w-6 h-6 text-neutral-400" />
                <span className="text-xl">Банкетный зал "Нежность"</span>
              </div>
              <p className="text-neutral-600 text-lg">ул. Свадебная, 1</p>
            </div>
          </div>

          <div className="pt-8">
            <h2 className="font-script text-4xl mb-8">Анкета гостя</h2>
            <p className="text-neutral-600 text-lg mb-8">
              Будем сильно рады видеть вас на этом празднике жизни!
            </p>
            <button
              onClick={() => setConfirmed(!confirmed)}
              className={`px-12 py-4 rounded-md transition-all text-lg
                ${confirmed 
                  ? 'bg-neutral-200 text-neutral-800' 
                  : 'bg-neutral-800 text-white'
                }`}
            >
              {confirmed ? 'Подтверждено' : 'Ответить'}
            </button>
          </div>
        </div>
      </Page>
    </div>
  );
};

// Add custom CSS for fonts
const CustomStyles = () => {
  return (
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }
    `}</style>
  );
};

const WeddingInvitationWithStyles = () => (
  <>
    <CustomStyles />
    <WeddingInvitation />
  </>
);

export default WeddingInvitationWithStyles;
