import React, { useState, useEffect } from 'react';
import { MapPin } from 'lucide-react';

const Countdown = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const calculateTimeLeft = () => {
      const weddingDate = new Date('2025-06-01T00:00:00');
      const now = new Date();
      const difference = weddingDate - now;

      if (difference > 0) {
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);

        setTimeLeft({ days, hours, minutes, seconds });
      }
    };

    const timer = setInterval(calculateTimeLeft, 1000);
    return () => clearInterval(timer);
  }, []);

  const TimeUnit = ({ value, label }) => (
    <div className="flex flex-col items-center mx-2 sm:mx-4">
      <div className="text-2xl sm:text-3xl font-light">{value}</div>
      <div className="text-xs sm:text-sm text-gray-600 mt-1">{label}</div>
    </div>
  );

  return (
    <div>
      <div className="text-center text-xl sm:text-2xl font-light mb-4">
        Au mai rămas:
      </div>
      <div className="flex justify-center items-center">
        <TimeUnit value={timeLeft.days} label="days" />
        <TimeUnit value={timeLeft.hours} label="hours" />
        <TimeUnit value={timeLeft.minutes} label="minutes" />
        <TimeUnit value={timeLeft.seconds} label="seconds" />
      </div>
    </div>
  );
};

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const CrucifixIcon = () => (
    <svg width="24" height="36" viewBox="0 0 24 36" className="inline-block">
      <path
        d="M10 2 L14 2 L14 8 L20 8 L20 12 L14 12 L14 34 L10 34 L10 12 L4 12 L4 8 L10 8 Z"
        fill="#666666"
        stroke="none"
      />
    </svg>
  );

  const Page = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex items-center justify-center p-4 sm:p-8 relative bg-[#fbfbf0] ${className}`}>
      <div className="max-w-4xl w-full">
        {children}
      </div>
    </div>
  );

  const Timeline = () => {
    const events = [
      { time: "11:00", icon: <CrucifixIcon />, label: "Slujba", x: 100, y: 450 },
      { time: "12:00", icon: "🥂", label: "Фуршет", x: 300, y: 300 },
      { time: "13:00", icon: "💍", label: "Свадебная церемония", x: 200, y: 200 },
      { time: "14:00", icon: "🍽️", label: "Праздничный ужин", x: 400, y: 150 },
      { time: "20:00", icon: "🎂", label: "Завершение вечера", x: 500, y: 50 }
    ];

    return (
      <div className="relative w-full max-w-[600px] h-[600px] mx-auto overflow-x-hidden">
        <svg 
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 600 600"
          style={{ zIndex: 0 }}
        >
          <path
            d="M 100 450 
               C 200 450, 350 350, 300 300
               S 150 250, 200 200
               S 350 180, 400 150
               S 450 100, 500 50"
            stroke="#939393"
            strokeWidth="1"
            strokeDasharray="4 4"
            fill="none"
          />
        </svg>

        {events.map((event, index) => (
          <div
            key={index}
            className="absolute"
            style={{
              left: `${event.x - 100}px`,
              top: `${event.y - 10}px`,
              width: '200px',
              transform: 'scale(0.8) translateX(-50%)',
              transformOrigin: 'left center'
            }}
          >
            <div className="absolute left-0 text-left -translate-x-[120px]">
              <div className="text-lg sm:text-xl font-light">{event.time}</div>
              <div className="italic text-xs sm:text-sm text-gray-700 mt-1">{event.label}</div>
            </div>

            <div 
              className="absolute text-xl sm:text-2xl"
              style={{
                left: '100px',
                transform: 'translateY(-50%)'
              }}
            >
              {event.icon}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="font-['Raleway'] min-h-screen flex flex-col">
      <Page>
        <div className="max-w-2xl mx-auto px-4 sm:px-8 text-center">
          <div className="mb-12 sm:mb-16">
            <h1 className="text-2xl sm:text-3xl font-light tracking-widest uppercase mb-4">
              SAVE THE DATE
            </h1>
          </div>
          
          <div className="mb-12 sm:mb-16">
            <div className="relative">
              <div className="absolute -left-12 sm:-left-16 top-0 opacity-50">
                <svg width="80" height="80" viewBox="0 0 100 100" className="w-10 h-10 sm:w-16 sm:h-16">
                  <path 
                    d="M20 50 Q50 20, 80 50 T140 50" 
                    fill="none" 
                    stroke="#C0C0C0" 
                    strokeWidth="2" 
                    strokeDasharray="5,5"
                  />
                </svg>
              </div>
              <div className="absolute -right-12 sm:-right-16 top-0 opacity-50 transform scale-x-[-1]">
                <svg width="80" height="80" viewBox="0 0 100 100" className="w-10 h-10 sm:w-16 sm:h-16">
                  <path 
                    d="M20 50 Q50 20, 80 50 T140 50" 
                    fill="none" 
                    stroke="#C0C0C0" 
                    strokeWidth="2" 
                    strokeDasharray="5,5"
                  />
                </svg>
              </div>
              <div className="flex flex-col items-center">
                <h2 className="font-script text-5xl sm:text-6xl mb-2">
                  Adina
                </h2>
                <div className="text-3xl sm:text-4xl mb-2 text-gray-400">&</div>
                <h2 className="font-script text-5xl sm:text-6xl">
                  Mihai
                </h2>
              </div>
            </div>
          </div>
          
          <div className="mb-12 sm:mb-16">
            <h1 className="text-2xl sm:text-3xl font-light tracking-widest uppercase">
              9 IUNIE 2025
            </h1>
          </div>
          
          <div className="text-sm sm:text-base text-gray-600 italic">
            Fond mult și bine!
          </div>
        </div>
      </Page>

      <Page>
        <div className="text-center mb-8 sm:mb-16">
          <h2 className="text-2xl sm:text-3xl tracking-[0.2em]">Program</h2>
        </div>
        <Timeline />
      </Page>

      <Page>
        <div className="max-w-2xl mx-auto text-center space-y-12 sm:space-y-16">
          <div>
            <h2 className="font-script text-3xl sm:text-4xl mb-6 sm:mb-8">Location</h2>
            <div className="space-y-4">
              <div className="flex items-center justify-center gap-2">
                <MapPin className="w-5 h-5 sm:w-6 sm:h-6 text-neutral-400" />
                <span className="text-lg sm:text-xl">Банкетный зал "Нежность"</span>
              </div>
              <p className="text-neutral-600 text-base sm:text-lg">ул. Свадебная, 1</p>
            </div>
          </div>

          <div className="pt-6 sm:pt-8">
            <h2 className="font-script text-3xl sm:text-4xl mb-6 sm:mb-8">Анкета гостя</h2>
            <p className="text-neutral-600 text-base sm:text-lg mb-6 sm:mb-8">
              Будем сильно рады видеть вас на этом празднике жизни!
            </p>
            <button
              onClick={() => setConfirmed(!confirmed)}
              className={`px-8 sm:px-12 py-3 sm:py-4 rounded-md transition-all text-base sm:text-lg
                ${confirmed 
                  ? 'bg-neutral-200 text-neutral-800' 
                  : 'bg-neutral-800 text-white'
                }`}
            >
              {confirmed ? 'Подтверждено' : 'Ответить'}
            </button>
          </div>
        </div>
      </Page>
    </div>
  );
};

const AnimatedBackground = () => {
  return (
    <div className="fixed inset-0 pointer-events-none">
      <svg className="w-full h-full" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <radialGradient id="heart-gradient" cx="50%" cy="50%" r="50%" fx="50%" fy="50%">
            <stop offset="0%" stopColor="#FFD1DC" stopOpacity="0.6" />
            <stop offset="100%" stopColor="#FFD1DC" stopOpacity="0" />
          </radialGradient>
        </defs>
        {[...Array(20)].map((_, index) => (
          <g key={index} className={`heart-container animate-float-${index % 5 + 1}`}>
            <path
              d="M10,30 A20,20,0,0,1,50,30 A20,20,0,0,1,90,30 Q90,60,50,90 Q10,60,10,30 Z"
              fill="url(#heart-gradient)"
              transform={`translate(${Math.random() * 100}%, ${Math.random() * 100}%) scale(${0.5 + Math.random() * 0.5})`}
            />
          </g>
        ))}
      </svg>
    </div>
  );
};

const CustomStyles = () => {
  return (
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }

      @keyframes float {
        0%, 100% { transform: translateY(0); }
        50% { transform: translateY(-20px); }
      }

      .animate-float-1 { animation: float 8s ease-in-out infinite; }
      .animate-float-2 { animation: float 9s ease-in-out infinite 1s; }
      .animate-float-3 { animation: float 10s ease-in-out infinite 2s; }
      .animate-float-4 { animation: float 11s ease-in-out infinite 3s; }
      .animate-float-5 { animation: float 12s ease-in-out infinite 4s; }
    `}</style>
  );
};

const WeddingInvitationWithStyles = () => (
  <div className="bg-white p-4 sm:p-8 min-h-screen flex items-center justify-center relative overflow-hidden">
    <AnimatedBackground />
    <div className="border-4 border-neutral-300 shadow-lg w-[90vw] max-w-[1000px] mx-auto relative z-10 bg-[#fbfbf0] bg-opacity-95">
      <CustomStyles />
      <WeddingInvitation />
    </div>
  </div>
);

export default WeddingInvitationWithStyles;
