import React, { useState, useEffect } from 'react';
import { MapPin } from 'lucide-react';

const Countdown = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const calculateTimeLeft = () => {
      const weddingDate = new Date('2025-06-01T00:00:00');
      const now = new Date();
      const difference = weddingDate - now;

      if (difference > 0) {
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);

        setTimeLeft({ days, hours, minutes, seconds });
      }
    };

    const timer = setInterval(calculateTimeLeft, 1000);
    return () => clearInterval(timer);
  }, []);

  const TimeUnit = ({ value, label }) => (
    <div className="flex flex-col items-center mx-2 sm:mx-4">
      <div className="text-2xl sm:text-3xl font-light">{value}</div>
      <div className="text-xs sm:text-sm text-gray-600 mt-1">{label}</div>
    </div>
  );

  return (
    <div>
      <div className="text-center text-xl sm:text-2xl font-light mb-4">
        Au mai rƒÉmas:
      </div>
      <div className="flex justify-center items-center">
        <TimeUnit value={timeLeft.days} label="days" />
        <TimeUnit value={timeLeft.hours} label="hours" />
        <TimeUnit value={timeLeft.minutes} label="minutes" />
        <TimeUnit value={timeLeft.seconds} label="seconds" />
      </div>
    </div>
  );
};

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const CrucifixIcon = () => (
    <svg width="24" height="36" viewBox="0 0 24 36" className="inline-block">
      <path
        d="M10 2 L14 2 L14 8 L20 8 L20 12 L14 12 L14 34 L10 34 L10 12 L4 12 L4 8 L10 8 Z"
        fill="#666666"
        stroke="none"
      />
    </svg>
  );

  const Page = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex items-center justify-center p-4 sm:p-8 relative bg-[#fbfbf0] ${className}`}>
      <div className="max-w-4xl w-full">
        {children}
      </div>
    </div>
  );

  const Timeline = () => {
    const events = [
      { time: "11:00", icon: <CrucifixIcon />, label: "Slujba", x: 100, y: 450 },
      { time: "12:00", icon: "ü•Ç", label: "–§—É—Ä—à–µ—Ç", x: 300, y: 300 },
      { time: "13:00", icon: "üíç", label: "–°–≤–∞–¥–µ–±–Ω–∞—è —Ü–µ—Ä–µ–º–æ–Ω–∏—è", x: 200, y: 200 },
      { time: "14:00", icon: "üçΩÔ∏è", label: "–ü—Ä–∞–∑–¥–Ω–∏—á–Ω—ã–π —É–∂–∏–Ω", x: 400, y: 150 },
      { time: "20:00", icon: "üéÇ", label: "–ó–∞–≤–µ—Ä—à–µ–Ω–∏–µ –≤–µ—á–µ—Ä–∞", x: 500, y: 50 }
    ];

    return (
      <div className="relative w-full max-w-[600px] h-[600px] mx-auto overflow-x-hidden">
        {/* SVG for curved dotted line */}
        <svg 
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 600 600"
          style={{ zIndex: 0 }}
        >
          <path
            d="M 100 450 
               C 200 450, 350 350, 300 300
               S 150 250, 200 200
               S 350 180, 400 150
               S 450 100, 500 50"
            stroke="#939393"
            strokeWidth="1"
            strokeDasharray="4 4"
            fill="none"
          />
        </svg>

        {/* Events */}
        {events.map((event, index) => (
          <div
            key={index}
            className="absolute"
            style={{
              left: `${event.x - 100}px`,
              top: `${event.y - 10}px`,
              width: '200px',
              transform: 'scale(0.8) translateX(-50%)',
              transformOrigin: 'left center'
            }}
          >
            {/* Time and Label */}
            <div className="absolute left-0 text-left -translate-x-[120px]">
              <div className="text-lg sm:text-xl font-light">{event.time}</div>
              <div className="italic text-xs sm:text-sm text-gray-700 mt-1">{event.label}</div>
            </div>

            {/* Icon */}
            <div 
              className="absolute text-xl sm:text-2xl"
              style={{
                left: '100px',
                transform: 'translateY(-50%)'
              }}
            >
              {event.icon}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
