import React, { useState, useEffect } from 'react';
import { MapPin } from 'lucide-react';

// ... (keep all the existing components: Countdown, WeddingInvitation, etc.)

const AnimatedFloralBackground = () => {
  return (
    <div className="fixed inset-0 pointer-events-none overflow-hidden">
      <svg className="w-full h-full" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <pattern id="floral-pattern" x="0" y="0" width="200" height="200" patternUnits="userSpaceOnUse">
            <g className="floral-element rotate-element">
              <path d="M0,0 C50,40 50,70 20,100 L0,85 L-20,100 C-50,70 -50,40 0,0" fill="#FFD1DC" opacity="0.6" />
              <path d="M0,0 C50,40 50,70 20,100 L0,85 L-20,100 C-50,70 -50,40 0,0" fill="#FFD1DC" opacity="0.6" transform="rotate(72, 0, 0)" />
              <path d="M0,0 C50,40 50,70 20,100 L0,85 L-20,100 C-50,70 -50,40 0,0" fill="#FFD1DC" opacity="0.6" transform="rotate(144, 0, 0)" />
              <path d="M0,0 C50,40 50,70 20,100 L0,85 L-20,100 C-50,70 -50,40 0,0" fill="#FFD1DC" opacity="0.6" transform="rotate(216, 0, 0)" />
              <path d="M0,0 C50,40 50,70 20,100 L0,85 L-20,100 C-50,70 -50,40 0,0" fill="#FFD1DC" opacity="0.6" transform="rotate(288, 0, 0)" />
            </g>
          </pattern>
        </defs>
        <rect x="0" y="0" width="100%" height="100%" fill="url(#floral-pattern)" />
      </svg>
    </div>
  );
};

const CustomStyles = () => {
  return (
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }

      @keyframes rotateFlower {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }

      .rotate-element {
        animation: rotateFlower 30s linear infinite;
        transform-origin: center;
      }

      .floral-element {
        opacity: 0.3;
      }
    `}</style>
  );
};

const WeddingInvitationWithStyles = () => (
  <div className="bg-white p-4 sm:p-8 min-h-screen flex items-center justify-center relative overflow-hidden">
    <AnimatedFloralBackground />
    <div className="border-4 border-neutral-300 shadow-lg w-[90vw] max-w-[1000px] mx-auto relative z-10 bg-[#fbfbf0] bg-opacity-95">
      <CustomStyles />
      <WeddingInvitation />
    </div>
  </div>
);

export default WeddingInvitationWithStyles;
