import React, { useState, useEffect } from 'react';

// Add custom fonts
const CustomFonts = () => {
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
      
      .font-script {
        font-family: 'Alex Brush', cursive;
      }
      
      .border-l-12 {
        border-left-width: 12px;
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);
  return null;
};
import { MapPin, Clock, Heart, Phone } from 'lucide-react';

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const weddingDetails = {
    brideAndGroom: {
      bride: "Sarah Johnson",
      groom: "Michael Smith",
      date: "August 15, 2025"
    },
    lyrics: "Love is patient, love is kind.\nIt does not envy, it does not boast,\nit is not proud.",
    ceremony: {
      time: "2:00 PM",
      location: "St. Patrick's Cathedral",
      address: "5th Avenue, New York, NY",
      mapsLink: "https://maps.google.com/?q=St+Patricks+Cathedral+NY"
    },
    reception: {
      time: "6:00 PM",
      location: "The Plaza Hotel",
      address: "768 5th Ave, New York, NY",
      mapsLink: "https://maps.google.com/?q=The+Plaza+Hotel+NY"
    },
    godparents: {
      bride: ["Jane & John Davis", "Mary & Robert Wilson"],
      groom: ["Elizabeth & James Brown", "Patricia & William Taylor"]
    },
    rsvpPhone: "+1 (555) 123-4567"
  };

  const Section = ({ children, className = "" }) => (
    <div className={`min-h-[100dvh] w-full flex flex-col items-center justify-center px-4 py-12 sm:p-8 ${className}`}>
      {children}
    </div>
  );

  return (
    <div className="font-['Raleway'] text-gray-800 overflow-x-hidden">
      <CustomFonts />
      {/* Hero Section */}
      <Section className="bg-neutral-100">
        <div className="w-full max-w-lg mx-auto text-center">
          <h1 className="text-4xl font-serif mb-12">INVITACIONES
            <span className="block font-script text-5xl -mt-2">Digitales</span>
          </h1>
          
          {/* Main Content */}
          <div className="relative mx-auto overflow-hidden bg-white rounded-lg shadow-lg" style={{ maxWidth: '320px' }}>
            <div className="text-center pt-8 pb-4 px-6">
              <h2 className="font-script text-3xl mb-2">Save the Date</h2>
              <p className="font-serif text-sm tracking-wider">20.01.2024</p>
              <p className="text-xs tracking-wide mt-1 text-neutral-600">MONTERREY, N.L.</p>
            </div>
            
            {/* Image Container */}
            <div className="relative">
              <img 
                src="/couple.jpg"
                alt="Bride and Groom" 
                className="w-full aspect-[3/4] object-cover grayscale"
              />
            </div>
            
            {/* Date Section */}
            <div className="absolute bottom-0 left-0 right-0 p-6 text-left bg-gradient-to-t from-white via-white/95 to-white/0">
              <div className="flex items-start">
                <div className="mr-4">
                  <div className="text-3xl font-bold">20</div>
                  <div className="text-3xl font-bold">01</div>
                  <div className="text-3xl font-bold">24</div>
                </div>
                <p className="text-sm leading-tight text-neutral-600 mt-1">
                  Nos llena de alegría compartir nuestro amor con ustedes. Los invitamos a celebrar junto a nosotros este día mas especial de nuestras vidas.
                </p>
              </div>
              <div className="mt-4 text-2xl font-script">Romina</div>
            </div>
          </div>
        </div>
      </Section>

      {/* Love Lyrics Section */}
      <Section className="bg-white">
        <div className="max-w-xl mx-auto text-center px-4">
          <Heart className="w-10 h-10 sm:w-12 sm:h-12 text-rose-500 mx-auto mb-6" />
          <p className="text-lg sm:text-xl md:text-2xl italic whitespace-pre-line">
            {weddingDetails.lyrics}
          </p>
        </div>
      </Section>

      {/* Ceremony Details Section */}
      <Section className="bg-rose-50">
        <div className="max-w-xl mx-auto text-center px-4">
          <h2 className="text-2xl sm:text-3xl mb-6 sm:mb-8">Ceremony Details</h2>
          
          <div className="mb-8 sm:mb-12">
            <h3 className="text-xl sm:text-2xl mb-4">Godparents</h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              <div>
                <h4 className="font-semibold mb-2">Bride's Godparents</h4>
                {weddingDetails.godparents.bride.map((pair, index) => (
                  <p key={index} className="mb-2 text-sm sm:text-base">{pair}</p>
                ))}
              </div>
              <div>
                <h4 className="font-semibold mb-2">Groom's Godparents</h4>
                {weddingDetails.godparents.groom.map((pair, index) => (
                  <p key={index} className="mb-2 text-sm sm:text-base">{pair}</p>
                ))}
              </div>
            </div>
          </div>

          <div className="space-y-6 sm:space-y-8">
            <div>
              <h3 className="text-xl sm:text-2xl mb-4">Religious Ceremony</h3>
              <div className="flex items-center justify-center mb-2">
                <Clock className="w-5 h-5 mr-2" />
                <span className="text-sm sm:text-base">{weddingDetails.ceremony.time}</span>
              </div>
              <div className="flex flex-col items-center">
                <MapPin className="w-5 h-5 mb-2" />
                <p className="font-semibold text-sm sm:text-base">{weddingDetails.ceremony.location}</p>
                <p className="text-sm sm:text-base">{weddingDetails.ceremony.address}</p>
                <a 
                  href={weddingDetails.ceremony.mapsLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-rose-500 hover:text-rose-600 mt-2 text-sm sm:text-base"
                >
                  View on Google Maps
                </a>
              </div>
            </div>

            <div>
              <h3 className="text-xl sm:text-2xl mb-4">Reception</h3>
              <div className="flex items-center justify-center mb-2">
                <Clock className="w-5 h-5 mr-2" />
                <span className="text-sm sm:text-base">{weddingDetails.reception.time}</span>
              </div>
              <div className="flex flex-col items-center">
                <MapPin className="w-5 h-5 mb-2" />
                <p className="font-semibold text-sm sm:text-base">{weddingDetails.reception.location}</p>
                <p className="text-sm sm:text-base">{weddingDetails.reception.address}</p>
                <a 
                  href={weddingDetails.reception.mapsLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-rose-500 hover:text-rose-600 mt-2 text-sm sm:text-base"
                >
                  View on Google Maps
                </a>
              </div>
            </div>
          </div>
        </div>
      </Section>

      {/* RSVP Section */}
      <Section className="bg-white">
        <div className="max-w-md mx-auto text-center px-4">
          <h2 className="text-2xl sm:text-3xl mb-6 sm:mb-8">Confirmation</h2>
          <div className="flex items-center justify-center space-x-4 mb-6">
            <Phone className="w-5 h-5 sm:w-6 sm:h-6" />
            <p className="text-lg sm:text-xl">{weddingDetails.rsvpPhone}</p>
          </div>
          <button
            onClick={() => setConfirmed(!confirmed)}
            className={`w-full sm:w-auto px-6 sm:px-8 py-3 rounded-full transition-colors text-sm sm:text-base ${
              confirmed 
                ? 'bg-green-500 text-white hover:bg-green-600' 
                : 'bg-rose-500 text-white hover:bg-rose-600'
            }`}
          >
            {confirmed ? 'Confirmed!' : 'Confirm Attendance'}
          </button>
        </div>
      </Section>
    </div>
  );
};

export default WeddingInvitation;
