import React, { useState, useEffect } from 'react';
import { MapPin, Clock, Heart, Phone } from 'lucide-react';

const WeddingInvitation = () => {
  const [confirmed, setConfirmed] = useState(false);

  const weddingDetails = {
    brideAndGroom: {
      bride: "Sarah",
      groom: "Michael",
      date: "August 15, 2025"
    },
    lyrics: "Love is patient, love is kind.\nIt does not envy, it does not boast,\nit is not proud.",
    ceremony: {
      time: "2:00 PM",
      location: "St. Patrick's Cathedral",
      address: "5th Avenue, New York, NY",
      mapsLink: "https://maps.google.com/?q=St+Patricks+Cathedral+NY"
    },
    reception: {
      time: "6:00 PM",
      location: "The Plaza Hotel",
      address: "768 5th Ave, New York, NY",
      mapsLink: "https://maps.google.com/?q=The+Plaza+Hotel+NY"
    },
    godparents: {
      bride: ["Jane & John Davis", "Mary & Robert Wilson"],
      groom: ["Elizabeth & James Brown", "Patricia & William Taylor"]
    },
    rsvpPhone: "+1 (555) 123-4567"
  };

  // Add custom fonts and animations
  const CustomStyles = () => {
    useEffect(() => {
      const style = document.createElement('style');
      style.textContent = `
        @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500&display=swap');
        @import url('https://fonts.googleapis.com/css2?family=Alex+Brush&display=swap');
        
        .font-script {
          font-family: 'Alex Brush', cursive;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes drawLine {
          from { width: 0; }
          to { width: 100%; }
        }

        .animate-fade-in {
          opacity: 0;
          animation: fadeIn 1.5s ease-out forwards;
        }

        .animate-draw-line {
          animation: drawLine 2s ease-out forwards;
        }

        .image-zoom {
          animation: zoomEffect 20s infinite alternate ease-in-out;
        }

        @keyframes zoomEffect {
          from { transform: scale(1); }
          to { transform: scale(1.1); }
        }

        .scroll-section {
          scroll-snap-align: start;
        }
      `;
      document.head.appendChild(style);
      return () => document.head.removeChild(style);
    }, []);
    return null;
  };

  const Section = ({ children, className = "" }) => (
    <div className={`min-h-screen w-full flex flex-col items-center justify-center p-8 scroll-section ${className}`}>
      {children}
    </div>
  );

  return (
    <div className="font-['Raleway'] text-gray-800 overflow-x-hidden">
      <CustomStyles />
      
      {/* Hero Section */}
      <Section className="bg-white">
        <div className="w-full max-w-4xl mx-auto text-center pt-12">
          {/* Title with animation */}
          <div className="animate-fade-in" style={{ animationDelay: '0.3s' }}>
            <h2 className="font-script text-4xl sm:text-5xl mb-2">Save the Date</h2>
            <p className="font-serif text-sm tracking-wider">20.01.2024</p>
            <p className="text-xs tracking-wide mt-1 text-neutral-600 mb-8">MONTERREY, N.L.</p>
          </div>
          
          {/* Decorative line */}
          <div className="mx-auto w-full max-w-xs h-px bg-neutral-200 mb-12 animate-draw-line"></div>
          
          {/* Image with overlay */}
          <div className="relative w-full max-w-2xl mx-auto overflow-hidden animate-fade-in" 
               style={{ animationDelay: '0.8s' }}>
            <div className="aspect-[4/3] relative overflow-hidden">
              <img 
                src="/couple.jpg"
                alt="Couple Sketch" 
                className="w-full h-full object-cover filter contrast-125 image-zoom"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>
            </div>
            
            {/* Bottom text overlay */}
            <div className="absolute bottom-0 left-0 right-0 p-8 text-center animate-fade-in"
                 style={{ animationDelay: '1.3s' }}>
              <div className="text-white space-y-4">
                <p className="text-sm leading-relaxed max-w-md mx-auto">
                  Nos llena de alegría compartir nuestro amor con ustedes. 
                  Los invitamos a celebrar junto a nosotros este día mas especial de nuestras vidas.
                </p>
                <div className="mt-4 font-script text-3xl">Romina & Daniel</div>
              </div>
            </div>
          </div>
          
          {/* Bottom date with animation */}
          <div className="mt-12 animate-fade-in"
               style={{ animationDelay: '1.8s' }}>
            <div className="flex justify-center items-center space-x-4">
              <div className="text-4xl font-light">
                20<span className="mx-2">|</span>01<span className="mx-2">|</span>24
              </div>
            </div>
          </div>
        </div>
      </Section>

      {/* Love Lyrics Section */}
      <Section className="bg-neutral-50">
        <div className="max-w-2xl mx-auto text-center">
          <Heart className="w-12 h-12 text-neutral-300 mx-auto mb-6" />
          <p className="text-xl md:text-2xl italic whitespace-pre-line text-neutral-600">
            {weddingDetails.lyrics}
          </p>
        </div>
      </Section>

      {/* Ceremony Details Section */}
      <Section className="bg-white">
        <div className="max-w-2xl mx-auto text-center">
          <h2 className="text-3xl font-script mb-12">Ceremony Details</h2>
          
          <div className="mb-16">
            <h3 className="text-2xl mb-8">Godparents</h3>
            <div className="grid md:grid-cols-2 gap-8">
              <div className="space-y-4">
                <h4 className="font-medium mb-4">Bride's Godparents</h4>
                {weddingDetails.godparents.bride.map((pair, index) => (
                  <p key={index} className="text-neutral-600">{pair}</p>
                ))}
              </div>
              <div className="space-y-4">
                <h4 className="font-medium mb-4">Groom's Godparents</h4>
                {weddingDetails.godparents.groom.map((pair, index) => (
                  <p key={index} className="text-neutral-600">{pair}</p>
                ))}
              </div>
            </div>
          </div>

          <div className="space-y-16">
            <div>
              <h3 className="text-2xl mb-6">Religious Ceremony</h3>
              <div className="flex items-center justify-center mb-4">
                <Clock className="w-5 h-5 mr-2 text-neutral-400" />
                <span>{weddingDetails.ceremony.time}</span>
              </div>
              <div className="flex flex-col items-center">
                <MapPin className="w-5 h-5 mb-2 text-neutral-400" />
                <p className="font-medium">{weddingDetails.ceremony.location}</p>
                <p className="text-neutral-600">{weddingDetails.ceremony.address}</p>
                <a 
                  href={weddingDetails.ceremony.mapsLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-neutral-400 hover:text-neutral-600 mt-2 text-sm"
                >
                  View on Google Maps
                </a>
              </div>
            </div>

            <div>
              <h3 className="text-2xl mb-6">Reception</h3>
              <div className="flex items-center justify-center mb-4">
                <Clock className="w-5 h-5 mr-2 text-neutral-400" />
                <span>{weddingDetails.reception.time}</span>
              </div>
              <div className="flex flex-col items-center">
                <MapPin className="w-5 h-5 mb-2 text-neutral-400" />
                <p className="font-medium">{weddingDetails.reception.location}</p>
                <p className="text-neutral-600">{weddingDetails.reception.address}</p>
                <a 
                  href={weddingDetails.reception.mapsLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-neutral-400 hover:text-neutral-600 mt-2 text-sm"
                >
                  View on Google Maps
                </a>
              </div>
            </div>
          </div>
        </div>
      </Section>

      {/* RSVP Section */}
      <Section className="bg-neutral-50">
        <div className="max-w-md mx-auto text-center">
          <h2 className="text-3xl font-script mb-8">Confirmation</h2>
          <div className="flex items-center justify-center space-x-4 mb-8">
            <Phone className="w-6 h-6 text-neutral-400" />
            <p className="text-xl text-neutral-600">{weddingDetails.rsvpPhone}</p>
          </div>
          <button
            onClick={() => setConfirmed(!confirmed)}
            className={`px-8 py-3 rounded-full transition-all transform hover:scale-105 text-sm
              ${confirmed 
                ? 'bg-green-500 text-white hover:bg-green-600' 
                : 'bg-neutral-800 text-white hover:bg-neutral-700'
              }`}
          >
            {confirmed ? 'Confirmed!' : 'Confirm Attendance'}
          </button>
        </div>
      </Section>
    </div>
  );
};

export default WeddingInvitation;
